mapper.select:


    tq.del_flag = '0'
     and tq.wjmc like concat('%', #{wjmc}, '%')

     and tq.ms = #{ms}

     and tq.nr = #{nr}
     and tq.begin_time = #{beginTime}
     and tq.end_time = #{endTime}
     and tq.sfnm = #{sfnm}

        AND tq.id IN (
        SELECT
        tqt.wjid
        FROM
        t_questionnaire_tag tqt
        WHERE
        tqt.del_flag = '0'
        AND tqt.bqid IN

            #{item}

        GROUP BY
        tqt.wjid

        )

    and (tq.kfcd = '0' or
    ( tq.kfcd = '1'	and	tqs.id IS NOT NULL))
    and tq.del_flag = '0'
    and tq.qyzt = '0'
    and tq.fb = '99'

    /*${params.dataScope}*/

    ${params.etpDataScope}

group by tq.id,tqs.wjnr
order by FIELD(status,'进行中','未开始','已完成','已结束'), tq.update_time desc

mapper.update:
update t_questionnaire
wjmc = #{wjmc}, qyzt = #{qyzt}, fm = #{fm}, ms = #{ms}, fb = #{fb}, nr = #{nr}, begin_time = #{beginTime}, end_time = #{endTime}, kfcd = #{kfcd}, sfnm = #{sfnm}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}
