mapper.update:
update t_task_student
rwid = #{rwid}, xyid = #{xyid}, jd = #{jd}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where rwid = #{rwid} and xyid=#{xyid}

mapper.select:
select t.jd->'$.lastStudy.current' as "currentId" from t_task_student t
where t.rwid= #{rwid} and xyid=#{xyid} and t.jd->"$.lastStudy.current" in

    #{kjid}
